// Extraire les données du body
const body = $input.item.json.body;
const { email, password, firstName, lastName } = body;

// Validation des champs requis
if (!email || !password || !firstName || !lastName) {
  return {
    json: {
      success: false,
      error: 'Tous les champs sont requis (email, password, firstName, lastName)'
    }
  };
}

// Validation format email
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(email)) {
  return {
    json: {
      success: false,
      error: 'Format d\'email invalide'
    }
  };
}

// Validation longueur password (min 8 caractères)
if (password.length < 8) {
  return {
    json: {
      success: false,
      error: 'Le mot de passe doit contenir au moins 8 caractères'
    }
  };
}

// Normaliser l'email en minuscules
return {
  json: {
    email: email.toLowerCase().trim(),
    password: password,
    firstName: firstName.trim(),
    lastName: lastName.trim(),
    validationPassed: true
  }
};

// Vérifier si l'email existe déjà
const emailCheckResult = $input.first().json;

if (emailCheckResult && emailCheckResult.id) {
  return {
    json: {
      success: false,
      error: 'Cet email est déjà utilisé'
    }
  };
}

// L'email n'existe pas, on peut continuer
const previousData = $('Validation').first().json;
return {
  json: {
    ...previousData,
    emailAvailable: true
  }
};

// Fonction pour générer un token aléatoire sans crypto
function generateToken(length) {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  let token = '';
  for (let i = 0; i < length; i++) {
    token += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return token + Date.now().toString(36);
}

const userData = $input.first().json;

// Générer des tokens aléatoires
const accessToken = generateToken(32);
const refreshToken = generateToken(32);

// Expiration: 15 minutes pour access_token
const expiresAt = new Date();
expiresAt.setMinutes(expiresAt.getMinutes() + 15);

return {
  json: {
    userId: userData.id,
    accessToken: accessToken,
    refreshToken: refreshToken,
    expiresAt: expiresAt.toISOString(),
    user: {
      id: userData.id,
      email: userData.email,
      firstName: userData.first_name,
      lastName: userData.last_name,
      isVerified: userData.is_verified,
      isActive: userData.is_active,
      createdAt: userData.created_at
    }
  }
};
